(()=>{var n=document.querySelector("nav"),l=n.querySelector("ul"),s=document.querySelector("#burger-template").content.cloneNode(!0),u=n.querySelector("svg"),o=s.querySelector("button");o.addEventListener("click",e=>{let t=o.getAttribute("aria-expanded")==="false";o.setAttribute("aria-expanded",t)});var r=()=>{o.setAttribute("aria-expanded",!1),o.focus()};n.addEventListener("keyup",e=>{e.code==="Escape"&&r()});document.addEventListener("click",e=>{n.contains(e.target)||r()});n.insertBefore(s,l);var a=[...document.querySelectorAll(".card")];a.forEach(e=>{e.style.cursor="pointer";let t,c,d=e.querySelector("a");e.onmousedown=()=>t=+new Date,e.onmouseup=()=>{c=+new Date,c-t<200&&d.click()}});var i=document.querySelector("#theme-toggle");i.addEventListener("click",()=>{document.body.classList.toggle("dark-mode");let e=document.body.classList.contains("dark-mode");localStorage.setItem("theme",e)});})();
